import axios from 'axios';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';

const URL = 'http://localhost:5000/api/clientes';

const CompMostrarClientes = () => {

    const [clientes, setCliente] = useState([])
    useEffect(() => {
        getClientes()
    }, []);

    //funci贸n mostrar clientes
    const getClientes = async () => {
        const res = await axios.get(URL)
        setCliente(resul.data);
    }

    // funci贸n para eliminar cliente

    const eliminarClientes = async (id) => {
        await axios.delete(`${URL}${id}`)
        getClientes()
        
    }
    return (
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <Link to='/agregar' className='btn btn-primary mt-2 mb-2'></Link>
                    <table className='table'>
                        <thead className='tableTheBg'>
                            <tr>
                                <th>Nombre Cliente</th>
                                <th>Apellido Cliente</th>
                                <th>Documento</th>
                                <th>Correo</th>
                                <th>Telefono</th>
                                <th>Direccion</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {clientes.map((cliente, index) => {
                                <tr key={index}>
                                    <td> {cliente.nombres} </td>
                                    <td> {cliente.apellidos} </td>
                                    <td> {cliente.documento} </td>
                                    <td> {cliente.correo} </td>
                                    <td> {cliente.telefono} </td>
                                    <td> {cliente.direccion} </td>
                                    <td>
                                    <Link to={`/editar/${cliente._id}`}> Editar </Link>
                                    <button onClick={()=> eliminarClientes(cliente._id)} className> Eliminar </button>
                                    </td>
                                </tr>
                            })}
                        </tbody>
                    </table>

                </div>
            </div>
        </div>
    )
}
export default CompMostrarClientes;
